import axios from 'axios'
import Header from './components/Header'
import Drawer from './components/Drawer'
import React from 'react'
import { Route, Routes } from 'react-router-dom';
import Home from './pages/Home';
import Favorites from './pages/Favorites';




function App() {
  const [items, setItems] = React.useState([])
  const [cartItems, setCartItems] = React.useState([])
  const [searchValue, setSearchValue] = React.useState('')
  const [cartOpened, setCartOpened] = React.useState(false)
  const [favorites, setFavorites] = React.useState([])

  React.useEffect(() => {
    // Get data from backEnd
    axios.get('https://626147bd327d3896e2787078.mockapi.io/items').then((res) => {
      setItems(res.data);
    });
    axios.get('https://626147bd327d3896e2787078.mockapi.io/cart').then((res) => {
      setCartItems(res.data || []);
    });
    axios.get('https://626147bd327d3896e2787078.mockapi.io/favorites').then((res) => {
      setFavorites(res.data);
    });
  }, []);

  // Add item to cart
  const onAddToCart = (obj) => {
    axios.post('https://626147bd327d3896e2787078.mockapi.io/cart', obj);
    setCartItems((prev) => [...prev, obj])
  }

  // Remove item from cart
  const onRemoveItem = (id) => {
    axios.delete(`https://626147bd327d3896e2787078.mockapi.io/cart/${id}`);
    setCartItems((prev) => prev.filter((item) => item.id !== id));
  }

  // Add / Remove like
  const onAddToFavorite = (obj) => {
    if (favorites.find(favObj.id === obj.id)) {
      axios.delete(`https://626147bd327d3896e2787078.mockapi.io/favorites/${obj.id}`);
      setFavorites((prev) => prev.filter((item) => item.id !== obj.id))
    } else {
      axios.post('https://626147bd327d3896e2787078.mockapi.io/favorites', obj);
      setFavorites((prev) => [...prev, obj])
    }
  }

  // Take input 
  const onChangeSearchInput = (event) => {

    setSearchValue(event.target.value)
  }





  return (

    <div className="wrapper clear ">

      {/* Cart Window */}
      {cartOpened && <Drawer
        items={cartItems}
        onClose={() => setCartOpened(false)}
        onRemove={onRemoveItem}
      />}

      <Header
        onClickCart={() => setCartOpened(true)}
      />

      {/* Pages */}
      <Routes>
        <Route path="/" element={
          <Home
            items={items}
            searchValue={searchValue}
            setSearchValue={setSearchValue}
            onChangeSearchInput={onChangeSearchInput}
            onAddToFavorite={onAddToFavorite}
            onAddToCart={onAddToCart}
          />}
        />

        <Route path="/favorites" element={
          <Favorites
            items={favorites}
            onAddToFavorite={onAddToFavorite}
            onAddToCart={onAddToCart}
          />}
        />

      </Routes>









    </div >

  )
}
export default App;
