import React from 'react';
import Card from '../components/Card';
import AppContext from '../context';
import Info from '../components/Info'
import { Link } from 'react-router-dom';
impo


function Favorites({ onAddToFavorite, onAddToCart }) {
   const { favorites } = React.useContext(AppContext);

   const backHomeBtn = (
      <Link to="/" >
         <button className="greenButtonBackHome">
            <img src='img/arrow.svg' alt='Arrow' className='mr-15' />
            Вернуться назад
         </button>
      </Link>)

   return (
      <div className="content p-40 " >
         {/* -- Title Of Favorites -- */}
         <div className="d-flex align-center justify-between mb-40 ">
            <h1> Избранное </h1>
         </div>
         {/* -- Favorite Cards -- */}
         <div className='d-flex flex-wrap '>
            {favorites.length > 0 ? favorites.map((item, index) => (
               <Card
                  key={index}
                  id={item.id}
                  title={item.title}
                  price={item.price}
                  imageUrl={item.imageUrl}
                  onFavorite={onAddToFavorite}
                  onPlus={(obj) => onAddToCart(obj)}
                  favorited={true}
               />
            )) : (<Info
               image={'img/favorites-empty.png'}
               title={'В избранных ничего нет'}
               discription={'Добавте сюда пару кроссовок,которые вам понравятся'}
               button={backHomeBtn}
            />)
            }
         </div>
      </div>
   )
}

export default Favorites
