import React from 'react';
import axios from 'axios';
import Card from '../components/Card';





function Orders() {
   const [isLoading, setIsLoading] = React.useState(true)
   const [orders, setOrders] = React.useState([])
   React.useEffect(() => {
      (async () => {
         try {
            const { data } = await axios.get(`https://6273f31a345e1821b224d416.mockapi.io/orders`)
            setOrders(data.map(obj => obj.items).flat())
            setIsLoading(false)
         } catch (error) {
            alert('Ошибка при запросе заказов')
            console.error(error);
         }
      })();
   }, []);

   return (

      <div className="content p-40 " >
         <div className="d-flex align-center justify-between mb-40 ">
            <h1> Заказы </h1>
         </div>
         <div className='d-flex flex-wrap '>
            {(isLoading ? [...Array(8)] : orders).map((item, index) => (
               <Card
                  key={index}
                  loading={isLoading}
                  {...item}
               />
            ))
            }

              (
               <div className="d-flex flex align-center justify-center flex-column" >
                  <img
                     className='m-40'
                     src="img/favorites-empty.png"
                     alt="Empty" />
                  <h2>В избранных ничего нет</h2>
                  <p className='opacity-6 text-center mb-40'>
                     Добавте сюда пару кроссовок,которые вам понравятся
                  </p>
                  <Link to="/">
                     <button className="greenButtonBackHome mb-50 ">
                        <img src='img/arrow.svg' alt='Arrow' className='mr-15' />
                        Вернуться назад
                     </button>
                  </Link>
               </div>
            )
         </div>
      </div>
   )
}

export default Orders
