import styles from './Info.module.scss';
import React from 'react';
import AppContext from '../../context';
import { Link } from 'react-router-dom';

const Info = ({ image, title, discription }) => {
   const { setCartOpened, cartOpened } = React.useContext(AppContext)

   return (

      <div className={styles.cartEmpty} >
         <img
            className='mb-20'
            src={image}
            alt="Empty" />
         <h2>{title}</h2>
         <p className='opacity-6 text-center mb-40'>
            {discription}
         </p>

         {cartOpened ?

            (<button onClick={() => setCartOpened(false)} className={styles.greenButton}>
               <img src='img/arrow.svg' alt='Arrow' className='mr-15' />
               Вернуться назад
            </button>)
            :
            (<Link to="/">
               <button className={styles.greenButtonBackHome}>
                  <img src='img/arrow.svg' alt='Arrow' className='mr-15' />
                  Вернуться назад
               </button>
            </Link>)
         }

      </div>
   )
}

export default Info;
