import React from 'react';
import AppContext from '../../context';


export const Info = (title, image, discription) => {
   const { setCartOpened } = React.useContext(AppContext);
   return (
      <div className='cartEmpty d-flex align-center jusify-center flex-column flex' >
         <img
            className='mb-20'
            width='120px'
            height='120px'
            src={image}
            alt="Empty" />
         <h2>{title}</h2>
         <p className='opacity-6'>
            {discription}
         </p>
         <button onClick={() => setCartOpened(false)} className={styles.greenButton}>
            <img src='../../public/img/arrow.svg' alt='Arrow' />
            Вернуться назад
         </button>
      </div>
   )
}

export default Info;
