import Card from './components/Card'
import axios from 'axios'
import Header from './components/Header'
import Drawer from './components/Drawer'
import React from 'react'



function App() {
  const [items, setItems] = React.useState([])
  const [cartItems, setCartItems] = React.useState([])
  const [searchValue, setSearchValue] = React.useState('')
  const [cartOpened, setCartOpened] = React.useState(false)

  React.useEffect(() => {
    axios.get('https://626147bd327d3896e2787078.mockapi.io/items').then(res => {
      setItems(res.data);
    });
    axios.get('https://626147bd327d3896e2787078.mockapi.io/cart').then(res => {
      setCartItems(res.data);
    });
  }, []);

  const onAddToCart = (obj) => {
    axios.post('https://626147bd327d3896e2787078.mockapi.io/cart', obj);
    setCartItems((prev) => [...prev, obj])
  }

  const onRemoveItem = (id) => {

    axios.delete(`https://626147bd327d3896e2787078.mockapi.io/cart/${id}`);
    setCartItems((prev) => prev.filter((item) => item.id !== id));
    console.log(id)
  }

  const onChangeSearchInput = (event) => {
    console.log(event.target.value);
    setSearchValue(event.target.value)
  }





  return (

    <div className="wrapper clear ">
      {cartOpened && <Drawer
        items={cartItems}
        onClose={() => setCartOpened(false)}
        onRemove={onRemoveItem}
      />}



      <Header
        onClickCart={() => setCartOpened(true)}

      />
      <div className="content p-40 " >
        {/* -- Title -- */}
        <div className="d-flex align-center justify-between mb-40 ">
          <h1> {searchValue ? `Поиск по запросу:${searchValue}` : 'Все кроссовки'} </h1>
          <div className="search-block d-flex ">
            <img src="img/search-icon.svg" alt="search-icon" />
            {searchValue && (
              <img
                className='clear cu-p'
                onClick={() => setSearchValue('')}
                src="/img/btn-remove.svg"
                alt="Close"
              />
            )}
            <input onChange={onChangeSearchInput} value={searchValue} placeholder="Поиск..." />
          </div>
        </div>
        {/* -- Cards -- */}
        <div className='d-flex flex-wrap '>
          {items
            .filter((item) => item.title.toLowerCase().includes(searchValue.toLowerCase()))
            .map((item, index) => (
              <Card
                key={index}
                title={item.title}
                price={item.price}
                imageUrl={item.imageUrl}
                onFavorite={() => console.log('like')}
                onPlus={(obj) => onAddToCart(obj)}
              />
            ))}
        </div>
      </div>
    </div >

  )
}
export default App;
