import axios from 'axios'
import Header from './components/Header'
import Drawer from './components/Drawer'
import React from 'react'
import { Route, Routes } from 'react-router-dom';
import Home from './pages/Home';
import Favorites from './pages/Favorites';
import AppContext from './context'

function App() {
  const [items, setItems] = React.useState([])
  const [cartItems, setCartItems] = React.useState([])
  const [favorites, setFavorites] = React.useState([])
  const [searchValue, setSearchValue] = React.useState('')
  const [cartOpened, setCartOpened] = React.useState(false)
  const [isLoading, setIsLoading] = React.useState(true)

  let mockApi = "https://626147bd327d3896e2787078.mockapi.io/"

  React.useEffect(() => {
    async function fetchData() {


      const cartResponse = await axios.get(`${mockApi}cart`);
      const favoritesResponse = axios.get(`${mockApi}favorites`);

      const itemsResponse = await axios.get(`${mockApi}items`);
      console.log(favoritesResponse.data)
      setIsLoading(false)

      setCartItems(cartResponse.data);
      setFavorites(favoritesResponse.data);
      setItems(itemsResponse.data);
    }
    fetchData()
  }, []);

  // Add item to cart
  const onAddToCart = (obj) => {
    try {

      if (cartItems.find((item) => Number(item.id) === Number(obj.id))) {
        axios.delete(`${mockApi}cart/${obj.id}`)
        setCartItems(prev => prev.filter((item) => Number(item.id) !== Number(obj.id)))
      } else {
        axios.post(`${mockApi}cart`, obj);
        setCartItems((prev) => [...prev, obj])
      }

    } catch (error) {
      alert('Не уалось добавить в корзину')
    }
  }

  // Remove item from cart
  const onRemoveItem = (id) => {
    axios.delete(`${mockApi}cart/${id}`);
    setCartItems((prev) => prev.filter((item) => item.id !== id));
  }

  // Add / Remove like
  const onAddToFavorite = async (obj) => {
    try {
      if (favorites.find((favObj) => Number(favObj.id) === Number(obj.id))) {
        axios.delete(`${mockApi}favorites/${obj.id}`);
        setFavorites((prev) => prev.filter((item) => item.id !== obj.id));
      } else {
        const { data } = await axios.post(`${mockApi}favorites`, obj);
        setFavorites((prev) => [...prev, data])
      }
    } catch (error) {
      alert('Не удалось добавить в избранное')
    }
  }

  // Take input 
  const onChangeSearchInput = (event) => {
    setSearchValue(event.target.value)
  }

  const isItemAdded = (id) => {
    return cartItems.some((obj) => Number(obj.id) == Number(id));
  }

  return (
    <AppContext.Provider
      value={{
        items,
        cartItems,
        favorites,
        isItemAdded,
        setCartOpened
      }}>

      <div className="wrapper clear ">

        {/* Cart Window */}
        {cartOpened && <Drawer
          items={cartItems}
          onClose={() => setCartOpened(false)}
          onRemove={onRemoveItem}
        />}

        <Header
          onClickCart={() => setCartOpened(true)}
        />

        <Routes>
          {/* Home Page */}
          <Route path="/" element={
            <Home
              items={items}
              cartItems={cartItems}
              searchValue={searchValue}
              setSearchValue={setSearchValue}
              onChangeSearchInput={onChangeSearchInput}
              onAddToFavorite={onAddToFavorite}
              onAddToCart={onAddToCart}
              isLoading={isLoading}
            />}
          />

          {/* Favorites Page */}
          <Route path="/favorites" element={
            <Favorites
              onAddToFavorite={onAddToFavorite}
            />}
          />
        </Routes>
      </div >
    </AppContext.Provider>
  )
}
export default App;
