import styles from './Drawer.module.scss'
import Info from '../Info'
import React from 'react';
import AppContext from '../../context';

function Drawer({ onClose, onRemove, items = [] }) {
   const { setCartItems } = React.useContext(AppContext);
   const [isOdrerComplite, setOrderCoplite] = React.useState(false);

   const onClickOrder = () => {
      setOrderCoplite(true)
      setCartItems([])
   };

   return (
      <div className={styles.overlay}>
         <div className={styles.drawer}>
            <h2>
               Корзина
               <img onClick={onClose} className={styles.removeBtn} src="/img/btn-remove.svg" alt="Remove" />
            </h2>

            {items.length > 0 ? (
               <div className='d-flex flex-column flex' >
                  <div className={styles.cartItems}  >
                     {items.map((obj) => (
                        <div key={obj.id} className={styles.cartItem} >
                           <div
                              style={{ backgroundImage: `url(${obj.imageUrl})` }}
                              className={styles.cartItemImg}>
                           </div>
                           <div className="mr-20 flex" >
                              <p className="mb-5" >{obj.title}</p>
                              <b>{obj.price}грн.</b>
                           </div>
                           <img
                              onClick={() => onRemove(obj.id)}
                              className={styles.removeBtn}
                              src="/img/btn-remove.svg"
                              alt="Remove" />
                        </div>
                     ))}
                  </div>
                  <div className={styles.cartTotalBlock} >
                     <ul >
                        <li >
                           <span>Итого:</span>
                           <div>
                           </div>
                           <b>21 498 грн.</b>
                        </li>
                        <li>
                           <span >Налог 5%:</span>
                           <div></div>
                           <b> 1174 грн. </b>
                        </li>
                     </ul>
                     <button onClick={onClickOrder} className={styles.greenButton}>
                        Оформить заказ
                        <img src='/img/arrow.svg' alt="arrow" />
                     </button>
                  </div>
               </div>
            ) : (
               <Info
                  title={isOdrerComplite ? "Заказ Выполнен" : "Корзина пустая"}
                  discription={isOdrerComplite ? "Ваш заказ №3 " :"Для покупки ножно добвить товар в корзину"}
                  image='../../../public/img/empty-cart.png'
               />
            )}
         </div>
      </div >
   );
}

export default Drawer;
